<script type="text/javascript">function closeAndSignIn(dcbID) {  spawnWindow(true, null, '50%', '50%', true, '0', '0', 'Log In / Join', 'static/signin.php');  var dcb = getDCBFromID(dcbID);  dcb.close();}$(document).ready(function() {  var topDivID = generateDivID("mainForumParent");  var topDiv = document.getElementById(topDivID);  var waitScreen = new StandbyScreen(document.getElementById("mainSection"));  var dcb = getDCBFromChild(topDiv);  $.ajax({    url: "services/manageQuests.php",      type: "POST",    data: {operation: "getForums"},    statusCode: {      200: function(data) {        var splitArray = data.split("|");        var questCounter = 0;        for (var i=0; i < splitArray.length-1; i++) {          var subSplit = splitArray[i].split("&");          var div = document.createElement("div");          var divClass;          var fontColor;          var hoverColor;          if (questCounter % 2 == 0) {            divClass = "grayRow";            fontColor = "#FFFFFF";            hoverColor = "#C8C8C8";          } else {            divClass = "brownRow";            fontColor = "#FFFFFF";            hoverColor = "#C8C8C8";          }          questCounter++;          $(div).attr("class", divClass);          $(div).data("questID", subSplit[0]);          $(div).data("title", escape(subSplit[1]));          $(div).data("fontColor", fontColor);          $(div).data("hoverColor", hoverColor);          $(div).css("color", fontColor);          $(div).css("width", "90%");          $(div).css("height", "32px");          $(div).css("margin-bottom", "5px");          $(div).css("padding-bottom", "1px");          $(div).css("text-align", "left");          $(div).css("float", "left");          $(div).css("display", "table");          $(div).css("box-shadow", "3px 3px 3px #888888");          var innerDiv = document.createElement("div");          $(innerDiv).css("display", "table-cell");          $(innerDiv).css("vertical-align", "middle");          var titleDiv = document.createElement("div");          $(titleDiv).css("padding-top", "1px");          $(titleDiv).css("width", "25%");          $(titleDiv).css("margin-left", "15px");          $(titleDiv).css("white-space", "normal");          $(titleDiv).css("overflow", "hidden");          $(titleDiv).css("float", "left");          $(titleDiv).html(subSplit[1]);          var descrDiv = document.createElement("div");          $(descrDiv).css("padding-right", "5px");          $(descrDiv).css("padding-top", "1px");          $(descrDiv).css("width", "50%");          $(descrDiv).css("white-space", "normal");          $(descrDiv).css("overflow", "hidden");          $(descrDiv).css("float", "left");          $(descrDiv).html(subSplit[2]);          $(innerDiv).append(titleDiv);          $(innerDiv).append(descrDiv);          $(div).append(innerDiv);          $(div).click(function() {            var url = "static/listSections.php?questTitle=" + escape($(this).data("title")) + "&questID=" + $(this).data("questID") + "&participant=forum";            spawnWindow(true, null, '80%', '80%', true, '0', '0', $(this).data("title") + "%20-%20Sections", url, "#F0F0F0", false, false, false);          });          $(div).hover(            function () {              $(this).css("cursor", "pointer");              $(this).css("color", $(this).data("hoverColor"));            },             function () {              $(this).css("color", $(this).data("fontColor"));            }          );          $(topDiv).append(div);        }        setTimeout(dcb.checkScrollers, 500, $(topDiv));        waitScreen.close();      },      404: function(data) {        $(topDiv).append("no forums exist just yet");        waitScreen.close();      },      401: function(data) {        $(topDiv).append("You must be logged in to view the forums.<br />");        $(topDiv).append("Click <u><a href=\"#\" onClick=\"javascript:closeAndSignIn('" + dcb.id + "');\">here</a></u> to sign in or create an account.");        waitScreen.close();      }    }  });})</script><div style="width:100%;height:100%;overflow:hidden;text-align:center;" id="mainForumParent"></div>