<script type="text/javascript">function makeCharacter() {  var selected = document.getElementById("systemSelect").selectedIndex;  var url;  switch(selected) {    case 0:    url = "php/createCharacterDND4.php";    break;    case 1:    url = "php/createCharacterDND3.php";    break;    case 2:    url = "php/createCharacterDND2.php";    break;  }  var dcb = getDCBFromChild(document.getElementById("mainCharDiv"));  spawnWindow('70%', '70%', true, '0', '0', 'Create Character', url);  dcb.close();}</script><div style="text-align:center;width:100%;height:100%;" id="mainCharDiv">Gaming System:&nbsp;<select id="systemSelect"><option>D&D 4.0</option><option>D&D 3.5</option><option>D&D 2.0</option><option>Custom</option></select><br /><div style="text-align:center;"><button type="button" onClick="javascript:makeCharacter();">Go</button></div></div>